changeSet.sql:
update product set unit='GRAM',unit_value='1000' where unit='KILO';
update product set unit='GRAM',unit_value='100' where unit='HUNDRED_GRAM';
update product set unit='GRAM',unit_value='250' where unit='TWO_FIFTY_GRAM';
update product set unit='GRAM',unit_value='250' where unit='TWO_FIFTY_GRAM';
update product set unit='LITRE',unit_value='1000' where unit='LITRE';
update product set unit='LITRE',unit_value='100' where unit='HUNDRED_MILLIS';
update product set unit='LITRE',unit_value='250' where unit='TWO_FIFTY_MILLIS';
update product set unit_value='0' where unit='PIECE';

CREATE TEXT SEARCH DICTIONARY russian_ispell (
TEMPLATE = ispell,
DictFile = russian,
AffFile = russian,
StopWords = russian
);
CREATE TEXT SEARCH CONFIGURATION ru ( COPY = russian );
ALTER TEXT SEARCH CONFIGURATION ru ALTER MAPPING FOR hword, hword_part, word WITH russian_ispell, russian_stem;
create index product_fts_idx on product using gin(to_tsvector('russian', name || ' ' || description));
create index value_fts_idx on property_value using gin(to_tsvector('russian', value));
create index property_fts_idx on property using gin(to_tsvector('russian', name));

update property set type='OPEN' where id in (1,8,9,11);
delete from property_value where root_value=true and property_id in (1,8,9,11);
update property_value set root_value=true where property_id in (1,8,9,11);
update property_value set client_generated=false;

update catalog set last_modified=now();
update product set last_modified=now();
update orders set last_modified=now();
update offer set last_modified=now();

update comment set entity_type='ORDER',entity_id=order_id where type='ORDER';
update comment set entity_type='USER',entity_id=user_id where type='CUSTOMER';
update comment set entity_type='CATALOG',entity_id=catalog_id where type='CATALOG';
delete from comment where parent_id!=null;

